
public class Array3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String proba = "Kaixo";
		char[] ariketa = proba.toCharArray(); //with toCharArray method we can save all the characters of a string in an array
		
		System.out.println(ariketa[0]); //as we can see the string has been separated in chars
		System.out.println(ariketa[1]);
		System.out.println(ariketa[2]);
		System.out.println(ariketa[3]);
		System.out.println(ariketa[4]);
		
	}

}
